class DateFormat(object):

    # 年
    YEAR = "yyyy"
    # 月
    MONTH = "MM"
    # 日
    DAY = "dd"
    # 时
    HOUR = "HH"
    # 分
    MINUTE = "mm"
    # 秒
    SECOND = "ss"
    # 毫秒
    MILLISECOND = "SSS"

    # 年月日
    YMD = "yyyy-MM-dd"
    # 年月日时分秒
    YMD_HMS = "yyyy-MM-dd HH:mm:ss"
    # 年月日时分秒毫秒
    YMD_HMS_M = "yyyy-MM-dd HH:mm:ss SSS"
    # 时分秒
    HMS = "HH:mm:ss"
    # 时分秒毫秒
    HMS_M = "HH:mm:ss SSS"
    # 月日
    MD = "MM-dd"
    # 月日时分秒
    MD_HMS = "MM-dd HH:mm:ss"
    # 月日时分秒毫秒
    MD_HMS_M = "MM-dd HH:mm:ss SSS"
    # 时分
    HM = "HH:mm"
