class Make3Channel:
    '''Tiles 1 channel image to 3 channel'''

    def __call__(self, x):
        if isinstance(x, Image):
            return x.convert(mode='RGB')
        rpts = (3, 1, 1) if x.ndim == 3 else (1, 3, 1, 1)
        return x.repeat(*rpts)
    
